mapper.update:
UPDATE goods
SET good_name = #{goodName},
    good_size = #{goodSize},
    good_price = #{goodPrice},
    good_num = #{goodNum}
WHERE good_id = #{goodId}

UPDATE goods
SET good_num = good_num + #{quantity}
WHERE good_id = #{id}

UPDATE goods
SET good_num = good_num - #{amount}
WHERE good_id = #{id} AND good_num >= #{amount}
